package com.itheima;import com.itheima.bean.Student;import com.itheima.mapper.StudentMapper;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.testng.annotations.Test;import java.io.InputStream;import java.util.ArrayList;import java.util.List;public class Test01 {    @Test    public static void selectCondition() throws Exception{        InputStream is = Resources.getResourceAsStream("MyBatisConfig.xml");        SqlSessionFactory sqlSessionFactory = new SqlSessionFactoryBuilder().build(is);        SqlSession sqlSession = sqlSessionFactory.openSession(true);        StudentMapper mapper = sqlSession.getMapper(StudentMapper.class);        Student stu = new Student();        stu.setId(2);        stu.setName("李四");        stu.setAge(24);        List<Student> list = mapper.selectCondition(stu);        for (Student stu1 : list) {            System.out.println(stu1);        }        is.close();        sqlSession.close();    }    @Test    public static void selectByIds() throws Exception{        InputStream is = Resources.getResourceAsStream("MyBatisConfig.xml");        SqlSessionFactory sqlSessionFactory = new SqlSessionFactoryBuilder().build(is);        SqlSession sqlSession = sqlSessionFactory.openSession(true);        StudentMapper mapper = sqlSession.getMapper(StudentMapper.class);        ArrayList<Integer> ids = new ArrayList<>();        ids.add(1);        ids.add(2);        List<Student> list = mapper.selectByIds(ids);        for (Student stu : list) {            System.out.println(stu);        }        is.close();        sqlSession.close();    }}